import React, { Fragment } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {logoutuser} from '../../actions/auth';
import {getUserProfile} from '../../actions/profile'
import PropTypes from 'prop-types';
import {getApplications} from '../../actions/applications';
import {AppBar, Container, Toolbar, Typography, IconButton, Button, Box} from '@material-ui/core'
import MenuIcon from '@material-ui/icons/Menu';
import { makeStyles } from '@material-ui/core/styles';




const Navbar = ({logoutuser, auth, getApplications, getUserProfile}) => {

  const useStyles = makeStyles((theme) =>({
    root: {
      flexGrow: 1
    },
    menuButton: {
      marginRight: theme.spacing(1)
    },
    title: {
      flexGrow: 1
    }
  }))

  const classes = useStyles();


    const {userVerified, loading, user} = auth;
    const userNotLoggedIn = (
      <Fragment>
          <Box mr={3}>
         <Button color="inherit" variant="outlined"><Link to="/register">Register</Link></Button>         
       </Box>
       <Button color="inherit" variant="outlined"><Link to="/login">Login</Link></Button>      
       </Fragment>
    );
    const userLoggedIn = (
      <Fragment>
        {user && user.who === 'Applicant' ? <li><Link onClick = {getApplications} to="/myapplication">My Applications</Link></li>: <Fragment/>}
        {user && user.who === 'Recruiter' ? <li><Link  to="/successjob">Employees</Link></li>: <Fragment/>}
        {/* {user && user.who === 'Recruiter' ? <li><Link  to="/applicants">Applicants</Link></li>: <Fragment/>} */}
      <li><Link onClick = {getUserProfile} to="/profile">Profile</Link></li>
      <li><Link to="/dashboard">Dashboard</Link></li>
      <li><Link to="/chat">Chat</Link></li>
      <li><Link to="/salary">Salary</Link></li>
      <li><Link onClick = {logoutuser} to="/"> <i className="fa fa-sign-out"></i> Logout</Link></li>
      </Fragment>
    );
    return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <h1>
      <Fragment>
       <Typography variant="h6" className={classes.title}> <Link to="/"><i className="fas fa-code"></i> DevConnector</Link></Typography>      
       </Fragment>
      {/* <Link to="/"><i className="fas fa-code"></i> DevConnector</Link> */}
      </h1>
      <ul>
        {
          (!loading && userVerified) ? (<Fragment>{userLoggedIn}</Fragment>) : (<Fragment>{userNotLoggedIn}</Fragment>) 
        }
      </ul>
    </nav>
    )
};

Navbar.propTypes = {
  logoutuser: PropTypes.func.isRequired,
  auth: PropTypes.func.isRequired,
  getApplications: PropTypes.func.isRequired,
}

const mapStateToProps = (state) => ({
  auth: state.authReducer,
});

export default connect(mapStateToProps, {logoutuser, getApplications, getUserProfile})(Navbar);
