import React, { Fragment } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {logoutuser} from '../../actions/auth';
import {getUserProfile} from '../../actions/profile'
import PropTypes from 'prop-types';
import {getApplications} from '../../actions/applications';
import "../../static/style.css";
import {Navbar, Nav, NavDropdown} from 'react-bootstrap';

const NavM = ({logoutuser, auth, getApplications, getUserProfile}) => {
    const {userVerified, loading, user} = auth;
    const userNotLoggedIn = (
      <Fragment>
        <li><Link to="/register">Register</Link></li>
        <li><Link to="/login">Login</Link></li>
      </Fragment>
    );
    const userLoggedIn = (
      <Fragment>

          <Navbar.Toggle />
      <Navbar.Collapse>
      <Nav>
        {user && user.who === 'Applicant' ? <li><Link onClick = {getApplications} to="/myapplication">My Applications</Link></li>: <Fragment/>}
        {user && user.who === 'Recruiter' ? <li><Link  to="/successjob">Employees</Link></li>: <Fragment/>}
        {/* {user && user.who === 'Recruiter' ? <li><Link  to="/applicants">Applicants</Link></li>: <Fragment/>} */}
      <li><Nav.Link onClick = {getUserProfile} to="/profile">Profile</Nav.Link></li>
      <li><Link to="/dashboard">Dashboard</Link></li>
      <li><Link to="/chat">Chat</Link></li>
      <li><Link to="/salary">Salary</Link></li>
      <li><Link onClick = {logoutuser} to="/"> <i className="fa fa-sign-out"></i> Logout</Link></li>
         </Nav>
      </Navbar.Collapse>                  

      </Fragment>

    );
    return (
      <Fragment>
      <Navbar bg="dark" variant="dark" sticky="top" expand="lg">
      <h1>
        <Navbar.Brand>
        <Link to="/"><i className="fas fa-code"></i> DevConnector</Link>
        </Navbar.Brand>
      </h1>     
      <ul>
        {
          (!loading && userVerified) ? (<Fragment>{userLoggedIn}</Fragment>) : (<Fragment>{userNotLoggedIn}</Fragment>) 
        }
      </ul>
      </Navbar>
      </Fragment>
    )
};

NavM.propTypes = {
  logoutuser: PropTypes.func.isRequired,
  auth: PropTypes.func.isRequired,
  getApplications: PropTypes.func.isRequired,
}

const mapStateToProps = (state) => ({
  auth: state.authReducer,
});

export default connect(mapStateToProps, {logoutuser, getApplications, getUserProfile})(NavM);
