import React, {Fragment} from 'react'
import PropTypes from 'prop-types'



const TemplateForProfile = ({profile}) => {
    const {
        name,
        email,
        contactnumber,
        bio,
        skills,

    } = profile;

    this.onChangeimage = this.onChangeimage.bind(this);

    function onChangeimage(event) {
        this.setState({ image: event.target.files[0] });
        const formData = new FormData();
        formData.append('file', event.target.files[0]);
            axios.post('http://localhost:4000/applicant/addfile?type=image&email=' + localStorage.getItem('user_email'), formData)
            .then(res => {
                console.log(res.json);
            })
            .catch(err => {
                console.log(err);
            });
    }

    return (
        <Fragment>
            <h2> Profile</h2>
            <table className='table'>
                    {name ?
                    <><Fragment>
                        <tr>
                              <th><img src={'http://localhost:4000/image/' + localStorage.getItem('user_image')} width='300' height='400'/></th>
                              <td><input
                                    type="file"
                                    onChange={this.onChangeimage}
                                    // value={this.state.image}
                                    id="image"
                                    name="image"/></td>
                        </tr>                                               
                        </Fragment><Fragment>
                        <tr>
                            <th>Name</th>
                            <td>{name}</td>
                        </tr>
                    </Fragment></>
            : 
            <Fragment> </Fragment>}
            {email ? 
            <Fragment>
                <tr>
                    <th>email</th>
                    <td>{email}</td>
                </tr>
            </Fragment>
            : 
            <Fragment> </Fragment>}
            {contactnumber ? 
            <Fragment>
                <tr>
                    <th>contactnumber</th>
                    <td>{contactnumber}</td>
                </tr>
            </Fragment>
            : 
            <Fragment> </Fragment>}
            {bio ? 
            <Fragment>
                <tr>
                    <th>bio</th>
                    <td>{bio}</td>
                </tr>
            </Fragment>
            : 
            <Fragment> </Fragment>}
            {skills ? 
            <Fragment>
                <tr>
                    <th>Skills</th>
                    <td>{skills}</td>
                </tr>
            </Fragment>
            : 
            <Fragment> </Fragment>}
            </table>

        </Fragment>

    );
}

TemplateForProfile.propTypes = {
    profile: PropTypes.array.isRequired
}

export default TemplateForProfile;
