import React , {useEffect, Fragment} from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux';
import { getApplications } from '../../actions/applications';
import { Recruiter } from '../../actions/types';
import {Link} from 'react-router-dom';
import {loaduserusingtoken} from '../../actions/auth';
import AllApplicants from '../applicants/AllApplicants';



const Dashboard = ({ getJobs, auth, applicationjobs,applications, getApplications, loaduserusingtoken}) => {
    
    useEffect(()=> {
        loaduserusingtoken();
        getJobs();
        getApplications();
      }, []);
    return (<Fragment>
            {loading || auth.loading || !auth.user || cjob!=null || (auth.user.who === 'Recruiter' && !jobs) || applications.loading ? <Fragment>Loading...</Fragment> : 
    <Fragment>

        <h1>Dashboard</h1>
            {auth.user && auth.user.who === Recruiter ? 
            <Fragment>
                <h3> 
                    <i className="fas fa-user"></i>
                    All applicants
                </h3>
                <AllApplicants applicationjobs={applicationjobs}/>
            </Fragment>
            :
            <Fragment>
               
            </Fragment>
            }
    </Fragment>}
    </Fragment>);
};

Dashboard.propTypes = {
    getJobs: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    applicationjobs: PropTypes.object.isRequired,
    loaduserusingtoken: PropTypes.func.isRequired,
    applications: PropTypes.object.isRequired,
    getApplications: PropTypes.func.isRequired,
}
const mapStateToProps = (state) => ({
    auth: state.authReducer,
    jobs: state.jobs,
    applications: state.applications, 
});

export default connect(mapStateToProps, { getJobs ,loaduserusingtoken, getApplications})(Dashboard);
