import React from 'react';
import {Fragment , useState} from 'react';
import {Link, Redirect} from 'react-router-dom';
import { connect } from 'react-redux';
import { loginuser } from '../../actions/auth';
import PropTypes from 'prop-types';

import axios from 'axios';
import {API_BASE_URL, ACCESS_TOKEN_NAME} from '../../constants/constants';
import { withRouter} from "react-router-dom";
import "../../static/style.css"

export const Login = ({loginuser, isUserVerified, user}) => {

    const [logindata, setlogindata] = useState({
        email: '',
        password: ''
      });
    const {email, password} = logindata;

    const onChange = (e) => {
      // console.log(e.target.value);
      setlogindata({ ...logindata, [e.target.name]: e.target.value});
    };

    const onSubmit = async (e) => {
      e.preventDefault();
      loginuser({email, password});
    }  
    if(isUserVerified && user)
    {
      return (
        <Redirect to='/dashboard'></Redirect>
      )
    }
    return (
        <Fragment>
    <main>

        <section class="right-container">
        <div class="content">        
          <div class="rectangle">
            </div>
        <h1  class="sign-in-text">Sign In</h1>
      <p><i className="fas fa-user"></i> Sign into  Your Account</p>
      <form onSubmit={e => onSubmit(e)}>

        <div class="input-container">
          <input type="email" className="form-control" placeholder="Email Address" name="email" class="email-input" value={email} onChange={e => onChange(e)}/>
          class="input-container"
        </div>
        <div className="form-group">
          <input
          className="form-control"
            type="password"
            placeholder="Password"
            name="password"
            minLength="6"
            required
            value={password} 
            onChange={e => onChange(e)}
            />
        </div>
        <input type="submit" className="btn btn-primary" value="Sign In" />
      </form>
      <p className="my-1">
        Don't have an account? <Link to="/Register">Sign Up</Link>
      </p>
      </div>
      </section>
  </main>
        </Fragment>
    )
}

Login.propTypes = {
  loginuser: PropTypes.func.isRequired,
  isUserVerified: PropTypes.bool,
  user: PropTypes.object.isRequired,
}

const mapStateToProps = (state) => ({
  isUserVerified: state.authReducer.userVerified,
  user: state.authReducer.user
});

export default connect(mapStateToProps, {loginuser})(Login);
